mapper.update:
UPDATE users
username = #{username}, email = #{email}, phone = #{phone}, age = #{age}, gender = #{gender}, is_vip = #{isVip}, update_time = NOW()
WHERE user_id = #{userId}

mapper.select:
SELECT SUM(total_amount) 
FROM orders 
WHERE user = #{userId}

    AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT COUNT(*) 
FROM orders 
WHERE user = #{userId}

    AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT AVG(total_amount) 
FROM orders 
WHERE user = #{userId}
AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT COUNT(DISTINCT user) 
FROM orders
WHERE create_time BETWEEN #{startTime} AND #{endTime}

SELECT DATE_FORMAT(o.create_time,
'%Y-%m-%d' '%Y-%u' '%Y-%m'
) AS timePoint, SUM(o.total_amount) AS amount, COUNT(o.order_id) AS count FROM orders o WHERE o.user = #{userId} AND o.create_time BETWEEN #{startTime} AND #{endTime} GROUP BY timePoint ORDER BY timePoint
